// src/utils/handleSessionExpiryUtility.js

export const handleSessionExpiryUtility = (navigate) => {
    // Define the localStorage keys you want to remove
    const localStorageKeys = [
        'token',
        'editMode',
        'noteContent',
        'currentNoteId',
        'chatLog',
    ];

    // Remove each key from localStorage
    localStorageKeys.forEach(key => localStorage.removeItem(key));

    // Alert the user about session expiry
    alert("Your session has expired. Please log in again.");

    // Use the passed `navigate` function to redirect the user to the login page
    navigate("/login");
};
