import {Link, useNavigate} from "react-router-dom";
import {useState} from "react";

export default function LoginBox({display, func}){
    let loginSection = null;
    const navigate = useNavigate();
    if(display){
        loginSection = (
            <>
                <p>Don't have an account?</p>
                <Link to="register">Register Now</Link>
            </>
        );
    }
    const handleButtonClick =()=>{
        if(display){


        }else{
            navigate("/login");
        }
    }


     const[userName, setUserName] = useState('');
     const[password, setPassword] = useState('');

        const handleSubmit = (event) => {
            //prevents the page loading after the event happened
            event.preventDefault();
            const input = {userName, password};

            //asynchronous, the server 8080 path
            fetch('http://localhost:8080/user/login/register',{
                method: 'POST',
                headers: {"Content-Type": "application/json"},
                body: JSON.stringify(input)
            }).then(response => {
                //server response
                if (response.ok) {
                    return response.text();
                }
                throw new Error('Network response was not ok.');
            }).then(data => {
                if (data) {
                    console.log("user posted", data);
                }
            }).catch(error => {
                    console.error("There has been a problem with your fetch operation:", error);
                });

        }

    return (
        <div className="login-container">
            <form onSubmit={handleSubmit} className="login-form">
                <h1>{func}</h1>
                <div className="input-group">
                    <label htmlFor="username">Username</label>
                    <input type="text" id="username" name="username" value={userName} onChange={(e) => setUserName(e.target.value)} required/>
                </div>
                <div className="input-group">
                    <label htmlFor="password">Password</label>
                    <input type="password" id="password" name="password" value={password} onChange={(e) => setPassword(e.target.value)} required/>
                    {!display && (
                        <>
                            <label htmlFor="confirmPassword">Confirm Password</label>
                            <input type="password" id="confirmPassword" name="confirmPassword" required/>
                        </>
                    )}
                </div>
                <button type="submit" >{func}</button>
                {loginSection}

            </form>
        </div>
    );

}
