import {useState} from "react";


export default function ChatBox(){

    const [input, setInput] = useState("");
    const [answer, setAnswer] = useState("");
    const [chatLog, setChatLog] = useState([]);
    const [isLoading, setIsLoading] = useState(false);

    const handleSend = async (event) => {
        event.preventDefault();
        setChatLog((prevChatLog) => [...prevChatLog, {type: 'user', message: input}])
        const rest_api = "http://localhost:8080/bot/chat?prompt=" + input;
        setIsLoading(true);
        try {   //my model is handled at the backend, as well as the key for security reasons
            const response = await fetch(rest_api, {
                method: 'GET',
                headers: {"Content-Type": "application/text"},
            });

            setChatLog((prevChatLog) => [...prevChatLog, {type: "bot", message: response.text()}])
            setIsLoading(false);
            setInput('');

            // setAnswer(textResponse);
        } catch (error) {
            setIsLoading(false)
            console.error("Fetching error: ", error);
        }
    };



    //<!--the button has to be in the form, for onSubmit to be triggered by the button
    //or the button can be outside the form, but then instead of having onSubmit={} we would have at the button onClick={handleSend}-->
    return (
        <div className="container mx-auto max-w-[700px]">
            <div className="flex flex-col h-screen bg-gray-900">
                <h1 className="bg-gradient-to-r from-blue-500 to-purple-500 text-transparent bg-clip-text text-center py-3 font-bold text-6xl">ChatBot</h1>
                <div className="flex-grow p-6">
                    <div className="flex flex-col space-y-4">
                        {//display all the messages
                            chatLog.map((message, index) => (<div key={index} className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}>
                                    <div className={`${
                                        message.type === 'user' ? 'bg-purple-500' : 'bg-gray-800'
                                    } rounded-lg p-4 text-white max-w-sm`}>
                                    {message.message}
                                    </div>
                                    </div>
                            ))
                        }
                    </div>
                </div>
                <form onSubmit={handleSend} className="flex-note p-6">
                    <div className="flex rounded-lg border border-gray-700 bg-gray-800">
                    <label htmlFor="note"></label>
                    <input clasName="flex-grow px-4 py-2 bg-transparent text-white focus:outline-none" name="note"  placeholder="Type your message..." value={input} onChange={(e) => {setInput(e.target.value)}}/>
                    <button type="submit" className="bg-purple-500 rounded-lg px-4 py-">Send</button>
                    </div>
                </form>
            </div>
        </div>
    )

}
