import {useState} from "react";


export default function ChatBox(){

    const [input, setInput] = useState("");

    const handleSend = async (event) => {
        console.log("aaa")
        event.preventDefault();
        const rest_api = "http://localhost:8080/bot/chat?prompt=" + input;
        try {
            const response = await fetch(rest_api, {
                method: 'GET',
                headers: {"Content-Type": "application/json"},
            });

            // Check if the response is JSON
            const contentType = response.headers.get("content-type");
            if (contentType && contentType.includes("application/json")) {
                const res = await response.json();
                console.log(res);
                console.log("mm")
                // Process JSON response
            } else {
                const textResponse = await response.text();
                console.log(textResponse);
                console.log("ddd")

                // Process text/plain response
            }
        } catch (error) {
            console.error("Fetching error: ", error);
        }
    };



    //<!--the button has to be in the form, for onSubmit to be triggered by the button
    //or the button can be outside the form, but then instead of having onSubmit={} we would have at the button onClick={handleSend}-->
    return (
        <div className="container">
            <div>
                <h1 id="titleNote">Text to bot :</h1>
                <form onSubmit={handleSend} className="note-form">
                    <label htmlFor="note"></label>
                    <textarea id="note" name="note" rows="10" cols="50" value={input} onChange={(e) => {setInput(e.target.value)}}/>
                    <button type="submit">Send</button>
                </form>
            </div>
            <div>
                <p>lll</p>
            </div>
        </div>
    )

}
