import {Link, useNavigate} from "react-router-dom";
import {useState} from "react";
import axios from "axios";
import {useRef} from "react";
const API_URL = "http://localhost:8080"


export default function DetailsBox({display, func}){
    let loginSection = null;

    const errRef = useRef();
    const[email, setEmail] = useState('');
    const[password, setPassword] = useState('');
    const[userName, setUserName] = useState('');


    const navigate = useNavigate();
    if(display){
        loginSection = (
            <>
                <p>Don't have an account?</p>
                <Link to="register">Register Now</Link>
            </>
        );
    }


    const handleRegister = async (event) =>{
        event.preventDefault();
        try{
            //axios makes data as json, no need to stringify it
           const response = await axios.post(API_URL + "/user/register", {userName, email, password},
                {
                    headers: {'Content-Type': 'application/json'},
                    withCredentials : true
                }
            );
           //axios does not have response.ok, but throws an error, if the response is not 200
            console.log("User posted", response.data)
        }catch (err){
            if(!err?.response){
                console.log("No Server Response");
            }else if(err.response?.status === 400){ //missing user details
                console.log("Error Message:", err.response.data);
            }else if(err.response?.status === 409){ //user already exists
                console.log("Error Message", err.response.data);
            }else{
                console.log("Registration failed");
            }
            errRef.current.focus();
        }
    }


     //using pure javascript
        const handleSubmit = async (event) => {
            //prevents the page loading after the event happened
            event.preventDefault();
            const input = {userName, email, password};


            //asynchronous, the server 8080 path, i put /register at the end, but if i remove /register from server i can remove it from here as well
            await fetch('http://localhost:8080/user/register',{
                method: 'POST',
                headers: {"Content-Type": "application/json"},
                body: JSON.stringify(input)
            }).then(response => {
                //server response
                if (response.ok) {
                    console.log("User posted")
                    return response.text();
                }
                throw new Error('HTTP error! status: ${response.status}');
            });

        }

    return (
        <div className="login-container">
            <form onSubmit={handleSubmit} className="login-form">
                <h1>{func}</h1>
                <div className="input-group">
                    <label htmlFor="email">Email</label>
                    <input type="text" id="email" name="email" value={email} onChange={(e) => setEmail(e.target.value)} required/>
                </div>
                    {!display && (
                        <div className="input-group">
                            <label htmlFor="userName">User name</label>
                            <input type="text" id="userName" name="userName" value={userName} onChange={(e) => setUserName(e.target.value)} required/>
                        </div>
                    )}
                <div className="input-group">
                    <label htmlFor="password">Password</label>
                    <input type="password" id="password" name="password" value={password} onChange={(e) => setPassword(e.target.value)} required/>
                    {!display && (
                        <div className="input-group">
                            <label htmlFor="confirmPassword">Confirm Password</label>
                            <input type="password" id="confirmPassword" name="confirmPassword" required/>
                        </div>
                    )}
                </div>
                <button type="submit" >{func}</button>
                {loginSection}

            </form>
        </div>
    );

}
