             method: 'POST',
             headers: {"Content-Type": "application/json"},
             body: JSON.stringify(input)
         }).then(response => {
             //server response
             if (response.ok) {
                 console.log("User posted")
                 return response.text();
             }
             throw new Error('HTTP error! status: ${response.status}');
         });

     }*/

}
