import {Link, useLocation, useNavigate} from "react-router-dom";
import {useContext, useEffect, useState} from "react";
import axios from "axios";
import {useRef} from "react";
import AuthContext from "../utils/AuthProvider";
const API_URL = "http://localhost:8080"


export default function DetailsBox({display, func}){

    const{setAuth} = useContext(AuthContext);
    const userRef = useRef();
    const errRef = useRef();
    const[email, setEmail] = useState('');
    const[password, setPassword] = useState('');
    const[userName, setUserName] = useState('');
    const[errMsg, setErrMsg] = useState('');
    const navigate = useNavigate();
    const location = useLocation();
    const from = location.state?.from || "/";


    function validateEmail(email) {
        let pattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return pattern.test(email);
    }

    function isPasswordStrong(password) {
        const minLength = 8;
        const hasUppercase = /[A-Z]/;
        const hasLowercase = /[a-z]/;
        const hasNumber = /[0-9]/;
        const hasSpecialChar = /[\s~`!@#$%\^&*+=\-\[\]\\';,/{}|\\":<>\?()\._]/; // Adjust based on required special characters

        return password.length >= minLength &&
            hasUppercase.test(password) &&
            hasLowercase.test(password) &&
            hasNumber.test(password) &&
            hasSpecialChar.test(password);
    }




    const handleRegister = async (event) =>{
        event.preventDefault();
        if(!validateEmail(email)){
            setErrMsg("Invalid email");
        }else {
            try {
                //axios makes data as json, no need to stringify it
                const response = await axios.post(API_URL + "/user/register", {userName, email, password},
                    {
                        headers: {'Content-Type': 'application/json'},
                    }
                );
                //axios does not have response.ok, but throws an error, if the response is not 200
                console.log("User posted", response.data)
                navigate("/login");
            } catch (err) {
                if (!err?.response) {
                    console.log("No Server Response");
                } else if (err.response?.status === 400) { //missing user details
                    setErrMsg(err.response.data);
                    console.log("Error Message:", err.response.data);
                } else if (err.response?.status === 409) { //user already exists
                    console.log("Error Message", err.response.data);
                    setErrMsg(err.response.data);
                } else {
                    setErrMsg("Registration failed");
                    console.log("Registration failed");
                }
                // errRef.current.focus();
            }
        }
    }


    const handleLogin = async (event) =>{
        event.preventDefault();
        try{
            //axios makes data as json, no need to stringify it
            const response = await axios.post(API_URL + "/user/authenticate", { email, password},
                {
                    headers: {'Content-Type': 'application/json'},
                }
            );
            //axios does not have response.ok, but throws an error, if the response is not 200
            console.log("User logged in", JSON.stringify(response.data));
            const accessToken = response.data?.token;
            setAuth({userName,email, password, accessToken});
            localStorage.setItem("accessToken", accessToken);
            setPassword("");
            setUserName("");
            setEmail("");
            navigate(from, {replace:true});
        }catch (err){
            if(!err?.response){
                console.log("No Server Response");
            }else if(err.response?.status === 403){ //missing user details
                setErrMsg("User does not exist");
                console.log("User does not exist");
            } else{
                setErrMsg("Login failed");
                console.log("Login failed");
            }
             errRef.current.focus();
        }
    }

    //happens only when the component loads
    useEffect(()=> {
        userRef.current.focus();
    }, [])

    useEffect(()=> {
        setErrMsg('');
    },[userName, email, password])


    return (
        <div className="login-container">
            <form onSubmit={display? handleLogin:handleRegister} className="login-form">
                <h1>{func}</h1>
                <p ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive">{errMsg}</p>
                <div className="input-group">
                    <label htmlFor="email">Email</label>
                    <input type="text" id="email" name="email" value={email} ref={userRef}  onChange={(e) => setEmail(e.target.value)} required/>
                </div>
                    {!display && (
                        <div className="input-group">
                            <label htmlFor="userName">User name</label>
                            <input type="text" id="userName" name="userName" value={userName} onChange={(e) => setUserName(e.target.value)} required/>
                        </div>
                    )}
                <div className="input-group">
                    <label htmlFor="password">Password</label>
                    <input type="password" id="password" name="password" value={password} onChange={(e) => setPassword(e.target.value)} required/>
                </div>
                <button type="submit" >{func}</button>
                {display && (
                    <div className="register-link">
                        <p>Don't have an account?</p>
                        <Link to="register">Register Now</Link>
                    </div>
                )
            }

            </form>
        </div>
    );



    //using pure javascript
   /* const handleSubmit = async (event) => {
        //prevents the page loading after the event happened
        event.preventDefault();
        const input = {userName, email, password};


        //asynchronous, the server 8080 path, i put /register at the end, but if i remove /register from server i can remove it from here as well
        await fetch('http://localhost:8080/user/register',{
            method: 'POST',
            headers: {"Content-Type": "application/json"},
            body: JSON.stringify(input)
        }).then(response => {
            //server response
            if (response.ok) {
                console.log("User posted")
                return response.text();
            }
            throw new Error('HTTP error! status: ${response.status}');
        });

    }*/


}
