const OpenAI = require('openai');
//const configuration = new OpenAI.Configuration({apiKey : "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"});
const openai = new OpenAI({apiKey: "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", dangerouslyAllowBrowser:true});


//sends a request to openAi server
//receive the response and extract it.
export async function sendMessageToOpenAI(message){
    const response = await openai.createCompletion({
        model: "text-davinci-003",
        prompt: message,
        max_tokens: 256,
        top_p: 1,
        frequency_penalty: 0,
        presence_penalty: 0
    });
    return response.data.choices[0].text; //extracting only the text
}
