import {useContext, useEffect, useState} from "react";
import AuthContext from "../utils/AuthProvider";
import axios from "axios";
import {useLocation, useNavigate} from "react-router-dom";
const API_URL = "http://localhost:8080"


export default function NoteBox(){

    const ITEMS_PER_PAGE = "4"; // Adjust this as needed


    let accessToken = localStorage.getItem('accessToken');
    const [isSidebarOpen, setIsSidebarOpen] = useState(false);
    const [sidebarData, setSidebarData] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [noteContent, setNoteContent] = useState(localStorage.getItem("noteContent") || '');
// Initialize editMode from localStorage or default to false if not set
    const [editMode, setEditMode] = useState(false);
    const toggleButtonContent = isSidebarOpen ? 'X' : 'Get saved notes';
    const[currentNoteId, setCurrentNoteId] = useState(null);
    //for pagiation
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(0);
    const [pagesCache, setPagesCache] = useState({});
    const navigate = useNavigate();
    const displayButton = editMode? 'Save Changes' : 'Save';



    useEffect(() => {
        // Assuming you're also saving currentNoteId to localStorage
        const savedNoteContent = localStorage.getItem("noteContent") || '';
        const savedEditMode = JSON.parse(localStorage.getItem("editMode") || "false");
        const savedNoteId = localStorage.getItem("currentNoteId"); // Ensure this is saved when setting editMode

        setNoteContent(savedNoteContent);
        setEditMode(savedEditMode);
        setCurrentNoteId(savedNoteId);
    }, []);


    const handleSubmit = async (event) => {
        //prevents the page loading after the event happened
        event.preventDefault();
        try {
            const response = await axios.post(API_URL + "/note", {
                content: noteContent
            }, {
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${accessToken}` // Include the token in the header
                }
            });
            setNoteContent("");
            if (response.status === 200 && isSidebarOpen) {
                console.log("mmm")
                await fetchData(currentPage);
            }

        }catch (err){
            if(!err?.response){
                console.log("No Server Response");
            }else if(err?.response.status === 401){
                console.log("Error message: ", err.response.data);
                localStorage.removeItem('token');
                alert("Your session has expired please log in again");
                navigate("/login")
            }else if(err?.response.status === 403){
                console.log("Bad request");
            }else{
                console.log("Failed to access source")
            }

        }
    }


    const editSubmit = async (event) =>{
        event.preventDefault();
        try {
            const response = await axios.put(API_URL + "/note/api/" + currentNoteId,  noteContent,{
                headers: {
                    'Content-Type': 'text/plain',
                    'Authorization': `Bearer ${accessToken}` // Include the token in the header
                }
            });
            setNoteContent("");
            setEditMode(false);
            if (response.status === 200 ) {
                await fetchData(currentPage);
            }
        }catch (err){
            if(!err?.response){
                console.log("No Server Response");
            }else if(err?.response.status === 401){
                console.log("Error message: ", err.response.data);
                localStorage.removeItem('token');
                alert("Your session has expired please log in again");
                navigate("/login")
            }else if(err?.response.status === 403){
                console.log("Bad request");
            }else{
                console.log("Failed to access source")
            }

        }

    }

    const fetchData = async (pageNumber = 1) => {
        const pageNum = pageNumber - 1;
        //if (pagesCache[pageNum]) {
          //  setSidebarData(pagesCache[pageNum].notes);
          //  setTotalPages(pagesCache[pageNum].totalPages);
          //  setCurrentPage(pagesCache[pageNum].currentPage);
           // setIsLoading(false);
        //} else {
            setIsLoading(true);
            try {
                const response = await axios.get(API_URL + "/note", {
                    params: {
                        page: pageNum,
                        size: ITEMS_PER_PAGE
                    },
                    headers: {
                        'Authorization': `Bearer ${accessToken}`
                    }
                });
               /* setPagesCache({
                    ...pagesCache,
                    [pageNum]: {
                        notes: response.data?.notes,
                        totalPages: response.data?.totalPages,
                        currentPage: response.data?.currentPage
                    }
                });*/
                console.log(response);
                setSidebarData(response.data?.notes);
                setTotalPages(response.data?.totalPages);
                setCurrentPage(response.data?.currentPage + 1);
                console.log("aaa");
                console.log(totalPages);
                console.log(currentPage);
                setIsLoading(false);
            } catch (error) {
                if(error?.response.status === 401) {
                    console.log("Error message: ", error.response.data);
                    localStorage.removeItem('token');
                    alert("Your session has expired please log in again");
                    navigate("/login")
                }else {
                    console.error('Error fetching data:', error);
                    setIsLoading(false);
                }
            }
        //}
    };




    //upon page load get the auth token as well, in case the user refreshes the page
    useEffect(() => {
        accessToken = localStorage.getItem('accessToken');

    }, []);

    const toggleSidebar = () => {
        setIsSidebarOpen(!isSidebarOpen);
        if (!isSidebarOpen) {
            setCurrentPage(1);
            fetchData();
        }
    };


    const handlePageClick = (pageNumber) => {
        setCurrentPage(pageNumber);
        fetchData(pageNumber);
    };


    const deleteRequest = async (noteId, event) => {
        let answer = window.confirm("Are you sure you want to delete the note?")
        if(answer){
        event.preventDefault();
        try{
            const response = await axios.delete(API_URL + "/note/" + noteId, {
                headers: {
                    'Authorization': `Bearer ${accessToken}`
                },
            });
            if (response.status === 200) { // 204 No Content is a common response for successful DELETE requests
                // Update the UI accordingly, maybe by re-fetching the current page of items
                await fetchData(currentPage);
            }
            console.log("aaaa")
        }catch (err){
            if(!err?.response){
                console.log("No Server Response");
            }else if(err?.response.status === 404){
                console.log("Error message: ", err.response.data);
                localStorage.removeItem('token');
                alert("Your session has expired please log in again");
                navigate("/login")
            }else if(err?.response.status === 403){
                console.log("Bad request");
            }else{
                console.log("Failed to access source")
            }

        }
        }

    };




    // Pagination Component
    const Pagination = ({ totalPages, currentPage }) => {
        const maxPageNumbersToShow = 3;
        const pages = [];
        // Calculate the range of page numbers to show
        // Calculate the range of page numbers to show
        let startPage = Math.max(1, currentPage - Math.floor(maxPageNumbersToShow / 2));
        let endPage = Math.min(totalPages, startPage + maxPageNumbersToShow - 1);

        // Adjust the start page if we're at the end of the range
        if (endPage === totalPages) {
            startPage = Math.max(1, endPage - maxPageNumbersToShow + 1);
        }

        // Previous Page Link
        pages.push(
            <button key="prev" className="buttonPag" onClick={() => handlePageClick(currentPage - 1)} disabled={currentPage === 1}>
                Previous
            </button>
        );

        // First Page Link (with ellipsis if necessary)
        if (startPage > 1) {
            pages.push(
                <a href="#" key={1} onClick={(e) => {
                    e.preventDefault();
                    handlePageClick(1);
                }}>
                    1
                </a>
            );
            if (startPage > 2) pages.push(<span key="startEllipsis">...</span>);
        }

        // Page Number Links
        for (let i = startPage; i <= endPage; i++) {
            pages.push(
                <a
                    href="#"
                    key={i}
                    className={`pagination-link ${i === currentPage ? 'active' : ''}`}
                    onClick={(e) => {
                        e.preventDefault();
                        handlePageClick(i);
                    }}
                    aria-current={i === currentPage ? 'page' : undefined}
                    aria-disabled={i === currentPage ? 'true' : 'false'}
                >
                    {i}
                </a>
            );
        }

        // Last Page Link (with ellipsis if necessary)
        if (endPage < totalPages) {
            if (endPage < totalPages - 1) pages.push(<span key="endEllipsis">...</span>);
            pages.push(
                <a href= "#" key={totalPages} onClick={(e) => {
                    e.preventDefault();
                    handlePageClick(totalPages);}}>
                    {totalPages}
                </a>
            );
        }

        // Next Page Link
        pages.push(
            <button key="next" className="buttonPag" onClick={() => handlePageClick(currentPage + 1)} disabled={currentPage === totalPages}>
                Next
            </button>
        );

        return <div className="pagination-container">{pages}</div>;
    };


    return (
        <div className="container-note">
            <div className='note-input-container'>
                <h1 id="titleNote">{editMode? 'Your Note' : 'Write your note here'}</h1>
                <form onSubmit={editMode ? editSubmit: handleSubmit} className="note-form">
                    <label htmlFor="noteContent"></label>
                    <textarea id="noteContent" name="noteContent" rows="10" cols="50" class="input-box-note" value={noteContent} onChange={(e) => setNoteContent(e.target.value)} />
                    <div className="note-buttons">
                    <button className="note-save" type="submit">{displayButton}</button>
                    {editMode && (
                        <button className="discard-save" onClick={() => {setEditMode(false); setNoteContent('')}}>Done Viewing</button>
                    )}
                </div>
                </form>
            </div>
            <div className="allSideBar">
                <button className={`sideBar-button ${toggleButtonContent}`} onClick={toggleSidebar}>{toggleButtonContent}</button>
                {isSidebarOpen && (
                    <div className="sidebar">
                        {isLoading ? (
                            <p>Loading...</p>
                        ) : (
                            <>

                                    <Pagination totalPages={totalPages} currentPage={currentPage} />

                                <div className="notes-container">
                                    {sidebarData && sidebarData.map((note, index) => (
                                        <div className="note-item" key={index}>
                                            <div className="note-date">{note.date}</div>
                                            <div className="note-content">{note.content}</div>
                                            <a href="#" className="edit" onClick={()=> {setNoteContent(note.content); setCurrentNoteId(note.id); setEditMode(true);}}>View</a>
                                            <a href="#" className="delete" onClick={(e)=> deleteRequest(note.id, e)}>Delete</a>
                                        </div>
                                    ))}
                                </div>
                            </>
                        )}
                    </div>
                    )}
            </div>
        </div>
    )


        //asynchronous, the server 8080 path, i put /register at the end, but if i remove /register from server i can remove it from here as well
