import {useState} from "react";
import TypingAnimation from "./TypingAnimation";
import axios from "axios";



export default function ChatBox(){

    const [input, setInput] = useState("");
    const [answer, setAnswer] = useState("");
    const [chatLog, setChatLog] = useState([]);
    const [isLoading, setIsLoading] = useState(false);

    const handleSend = async (event) => {
        event.preventDefault();
        setChatLog((prevChatLog) => [...prevChatLog, {type: 'user', message: input}])
        const rest_api = "http://localhost:8080/bot/chat?prompt=" + input;
        setIsLoading(true);
        try {   //my model is handled at the backend, as well as the key for security reasons
            const response = await fetch(rest_api, {
                method: 'GET',
                headers: {"Content-Type": "application/text"},
            });
            const responseText = await response.text()
            setChatLog((prevChatLog) => [...prevChatLog, {type: "bot", message: responseText}])
            setIsLoading(false);
            setInput('');

            // setAnswer(textResponse);
        } catch (error) {
            setIsLoading(false)
            console.error("Fetching error: ", error);
        }
    };



    //<!--the button has to be in the form, for onSubmit to be triggered by the button
    const handleSaveMessage = async (message, event)=> {
        event.preventDefault();
        try {
            const response = await axios.post(API_URL + "/note", {
                content: noteContent
            }, {
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${accessToken}` // Include the token in the header
                }
            });
            setNoteContent("");
            if (response.status === 200 && isSidebarOpen) {
                console.log("mmm")
                await fetchData(currentPage);
            }
        }catch (err){
            if(!err?.response){
                console.log("No Server Response");
            }else if(err?.response.status === 401){
                console.log("Error message: ", err.response.data);
            }else if(err?.response.status === 403){
                console.log("Bad request");
            }else{
                console.log("Failed to access source")
            }

        }


    }

    //or the button can be outside the form, but then instead of having onSubmit={} we would have at the button onClick={handleSend}-->
    return (
        <div className="container-chat">
            <header className="chat-header">
                ChatGPT
            </header>
            <div className="chat-messages">
                {chatLog.map((message, index) => (
                    <div key={index} className={`message ${message.type}`}>
                        <div className="message-bubble">
                            {message.message}
                        </div>
                        {message.type === 'bot' && <button className="save-chat-convo"
                         onClick={(e) => handleSaveMessage(message.message, e)}  >Save</button>}
                    </div>
                ))}
                {
                    isLoading && <div key={chatLog.length} className="typing-log">
                    <div className="animation-log">
                        <TypingAnimation/>
                    </div>
                    </div>
                }
            </div>
            <form onSubmit={handleSend} className="chat-input-area">
                <input
                    className="chat-input"
                    placeholder="Type your message..."
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                />
                <button type="submit" className="send-button">
                    Send
                </button>
            </form>
        </div>
    );
}
